import streamlit as st 

st. header("Programming Langugage Poll")

col1, col2, col3 = st.columns(3)

with col1:
    st.header("Java")
    st.image("data:image/png;base64,", width = 150)
    choice1 = st.button("Vote for Java")

with col2:
    st.header("C")
    st.image("data:image/png;base64,", width = 150)
    choice2 = st.button("Vote for C")

with col3:
    st.header("Js")
    st.image("data:image/png;base64,", width = 150)
    choice3 = st.button("Vote for Js")

if choice1:
    st.success("You have choose Java as your favorite programming language")

elif choice2:
        st.success("You have choose C as your favorite programming language")

elif choice3:
        st.success("You have choose Js as your favorite programming language")

name = st.sidebar.text_input("Enter your name")

st.write(f"Thank you {name} for choosing your favorite programming language!")
st.write("Wish You Luck")


